import React, { Component } from "react";
import { motion, useCycle } from "framer-motion";

class CardAnimate extends Component {
  constructor(props) {
    super(props);
  }
  state = {};
  render() {
    let cloneStyle = {
      position: "fixed",
      top: "0px",
      left: "0px",
      width: "100vw",
      height: "100vw",
      backgroundColor: "red",
    };
    // const cardStyle = {
    //   opacity: "0",
    // };
    console.log("Maah");
    console.log(this.props.children);
    return (
      <div
        onClick={React.cloneElement(
          this.props.children,
          [this.props, cloneStyle],
          null
        )}
      />
    );
  }
}

export default CardAnimate;

