import React, { Component } from "react";
import { motion, useCycle } from "framer-motion";
import "../Card.css";

class CardAnimate extends Component {
  constructor(props) {
    super(props);
    this.cardRefs = React.createRef();
  }
  state = {};
  render() {
    const {
      top,
      left,
      width,
      height,
    } = this.cardRefs.current.getBoundingClientRect();

    const cloneStartStyle = {
      position: "fixed",
      top: top + "px",
      left: left + "px",
      width: width + "px",
      height: height + "px",
      backgroundColor: "red",
    };
    // const cardStyle = {
    //   opacity: "0",
    // };
    console.log("Maah");
    console.log(this.props.children);
    return React.cloneElement(
      this.props.children,
      [this.props, cloneStartStyle, "cloneEndCard", this.cardRefs],
      null
    );
  }
}

export default CardAnimate;

