import React, { Component } from "react";
import Card from "./Card";
import ProjectData from "../ProjectData";
import CardAnimate from "./CardAnimated";
import "../Card.css";

class Project extends Component {
  state = {};
  handleClick = (event) => {
    let demo = "red";
    const card = event.currentTarget;
    console.log(card);
    return React.cloneElement(card, demo);

    // return (
    //   <span ref={this.cardRefs2}>
    //     React.cloneElement(this.cardRefs, this.props);
    //   </span>
    // );
  };
  render() {
    return (
      <section>
        <div
          id="projects"
          className="Projects"
          style={{ zIndex: 5, position: "absolute" }}
        >
          <div className="heading text-center">My Projects</div>

          <div className="container-fluid">
            <div className="row">
              <div className="col-12 mx-auto">
                <div className="row gy-4">
                  {ProjectData.map((val, index) => {
                    return (
                      <CardAnimate
                        key={index}
                        index={index}
                        imgsrc={val.imgsrc}
                        text={val.text}
                        title={val.title}
                        url={val.url}
                      >
                        <Card />
                      </CardAnimate>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default Project;

