import React, { useRef } from "react";
// import web from "../Images/d0a103.png";
// import { useHistory } from "react-router-dom";
// import { BrowserRouter, Link, NavLink } from "react-router-dom";

class Card extends Component {
  constructor(props) {
    super(props);
    this.cardRefs = {};
  }
  state = {};

  componentDidMount() {
    console.log(this.cardRefs);
  }
  handleClick = (event) => {
    const card = event.currentTarget;
    console.log(card);
    const cardClone = React.cloneElement(card, props);
    // const {
    //   top,
    //   left,
    //   width,
    //   height,
    // } = this.ref.inner.clientHeight
    console.log(this.ref.inner.clientHeight);
  };

  render(props) {
    return (
      <div
        className="col-md-4 col-10 mx-auto"
        ref={(ref) => (this.cardRefs[props.key] = ref)}
        onClick={handleClick}
      >
        <div className="card">
          <img src={props.imgsrc} className="card-img-top" alt={props.imgsrc} />
          <div className="card-body ">
            <h5 className="card-title font-weight-bold">{props.title}</h5>
            <p className="card-text">{props.text}</p>

            <a href={props.url} target="_blank" className="btn btn-primary">
              View Code
            </a>
          </div>
        </div>
      </div>
    );
  }
}

