import React, { Component } from "react";

// import web from "../Images/d0a103.png";
// import { useHistory } from "react-router-dom";
// import { BrowserRouter, Link, NavLink } from "react-router-dom";
class Card extends Component {
  constructor(props) {
    super(props);

    this.cardRefs = React.createRef();
    // this.handleClick = this.handleClick.bind(this);
  }
  state = {
    active: false,
  };

  handleClick = (event) => {
    const currentState = this.state.active;
    console.log(currentState);
    this.setState({ active: !currentState });
    console.log(this.state.active);
  };

  render() {
    const imageUrl = this.props.imgsrc;
    return (
      <div className=" col-md-4 col-10 mx-auto" onClick={this.handleClick}>
        {/* style={{ backgroundImage: `url(${imageUrl})` }} */}
        <div className={this.state.active ? "box-clicked" : "box"}>
          {/* <img src={props.imgsrc} className="card-img-top" alt={props.imgsrc} /> */}
          <div className="card-body ">
            <h5 className="card-title font-weight-bold">{this.props.title}</h5>
            <p className="card-text">{this.props.text}</p>

            <a
              href={this.props.url}
              target="_blank"
              className="btn btn-primary"
            >
              View Code
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default Card;

