import React, { Component } from "react";
import Card from "./Card";
import ProjectData from "../ProjectData";

class Project extends Component {
  state = {};
  // handleClick = (event) => {
  //   let demo = "red";
  //   const card = event.currentTarget;
  //   console.log(card);
  //   return React.cloneElement(card, demo);

  //   // return (
  //   //   <span ref={this.cardRefs2}>
  //   //     React.cloneElement(this.cardRefs, this.props);
  //   //   </span>
  //   // );
  // };
  render() {
    return (
      <section>
        <div id="projects" className="Projects">
          <div className="heading text-center">My Projects</div>

          <div className="container-fluid">
            <div className="row">
              <div className="col-10 mx-auto">
                <div className="row gy-4">
                  <div
                    className="col-md-4 col-10 mx-auto"
                    onClick={this.handleClick}
                  >
                    {ProjectData.map((val, index) => {
                      return (
                        <div className="card" ref={this.cardRefs}>
                          <img
                            src={this.props.imgsrc}
                            className="card-img-top"
                            alt={this.props.imgsrc}
                          />
                          <div
                            className="card-body"
                            style={{ color: this.props.demo }}
                          >
                            <h5 className="card-title font-weight-bold">
                              {this.props.title}
                            </h5>
                            <p className="card-text">{this.props.text}</p>

                            <a
                              href={this.props.url}
                              target="_blank"
                              className="btn btn-primary"
                            >
                              View Code
                            </a>
                          </div>
                        </div>
                        // <Card
                        //   key={index}
                        //   imgsrc={val.imgsrc}
                        //   text={val.text}
                        //   title={val.title}
                        //   url={val.url}
                        // />
                      );
                    })}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default Project;

