import React, { useRef, Component } from "react";
import ReactDOM from "react-dom";
// import web from "../Images/d0a103.png";
// import { useHistory } from "react-router-dom";
// import { BrowserRouter, Link, NavLink } from "react-router-dom";
import $ from "jquery";

class Card extends Component {
  constructor(props) {
    super(props);

    this.cardRefs = React.createRef();
    this.handleClick = this.handleClick.bind(this);
  }

  state = {
    active: false,
  };

  componentDidMount() {
    $(this.cardRefs).on("click", function () {
      $(this.cardRefs).addClass("cloneStartStyle");
    });
  }

  handleClick = (event) => {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
  };

  getClone = () => {
    this.cardRefs.onClick(function () {
      this.cardRefs.addClass("cloneStartStyle");
    });
  };

  render() {
    console.log(this.props);
    console.log(this.state.active);
    return (
      <>
        <div
          id="productCard"
          className="col-md-4 col-10 mx-auto "
          onClick={this.handleClick}
        >
          <div
            className="card"
            // {
            //   this.state.active ? "card clicked" : "card cloneStartStyle"
            // }
            // style={this.state.active ? this.props[1] : null}
            ref={this.cardRefs}
          >
            <img
              src={this.props[0].imgsrc}
              className="card-img-top"
              alt={this.props[0].imgsrc}
            />
            <div className="card-body">
              <h5 className="card-title font-weight-bold">
                {this.props[0].title}
              </h5>
              <p className="card-text">{this.props[0].text}</p>

              <a
                href={this.props[0].url}
                target="_blank"
                className="btn btn-primary"
              >
                View Code
              </a>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Card;

