import React from "react";
import Card from "./Card";
import ProjectData from "../ProjectData";
import CardAnimated from "./CardAnimated";
import { motion, useCycle } from "framer-motion";
const Project = () => {
  const [animate, toggleFocus] = useCycle(
    { height: "25rem", top: "0rem" },
    { height: "100%", top: "-4.35rem" }
  );
  return (
    <section>
      <div id="projects" className="Projects">
        <div className="heading text-center">My Projects</div>

        {/* <div className="container-fluid">
          <div className="row">
            <div className="col-10 mx-auto">
              <div className="row gy-4">
                {ProjectData.map((val, index) => {
                  return (
                    <div>
                      <Card
                        key={index}
                        imgsrc={val.imgsrc}
                        text={val.text}
                        title={val.title}
                        url={val.url}
                      />
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div> */}

        <div>
          <h1 className="title"> Showcase</h1>
          <motion.div
            className="card-container"
            animate={animate}
            initial={{ top: "0rem" }}
          >
            {ProjectData.map((val, index) => {
              return (
                <div>
                  <CardAnimated
                    key={index}
                    image={val.imgsrc}
                    title={val.title}
                    onToggleFocus={toggleFocus}
                  />
                </div>
              );
            })}
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Project;
