import React, { Component } from "react";
// import web from "../Images/d0a103.png";
// import { BrowserRouter, Link, NavLink } from "react-router-dom";
import "C:/Users/maaha/Desktop/Portfolio/node_modules/font-awesome/css/font-awesome.min.css";
// import "../Card.css";

class Card extends Component {
  constructor() {
    super();
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      active: false,
    };
  }

  handleClick() {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
  }

  render(props) {
    return (
      <>
        <div className="col-md-4 col-10 mx-auto">
          <div className="card">
            console.log({props.text})
            {/* <img
              src={props.imgsrc}
              className="card-img-top"
              alt={props.imgsrc}
            /> */}
            <div className="card-body Card">
              <h5 className="card-title font-weight-bold">{props.title}</h5>
              <p className="card-text">{props.text}</p>

              <a href={props.url} target="_blank" className="btn btn-primary">
                View Code
              </a>
            </div>
          </div>
        </div>
      </>
    );
  }
}

// <div
// className={this.state.active ? "book--expanded" : "book"}
// onClick={this.handleClick}
// >

export default Card;
