import "C:/Users/maaha/Desktop/Portfolio/node_modules/font-awesome/css/font-awesome.min.css";
import $ from "jquery";
import "../Card.css";

class Card extends Component {
  //watch this link: https://medium.com/@shuvohabib/using-jquery-in-react-component-the-refs-way-969de9aa651f
  //and make jquery work on react.
  //continue building animation for card
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      active: false,
    };
  }

  handleClick() {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
  }

  render() {
    return (
      <>
        <div className="wrapper">
          <div className="book">
            <div className="book__cover">
              <div className="header-image">
                <div className="overlay"></div>
              </div>
              <div className="title-wrap">
                <h1 className="article-title">EcoSwap</h1>
              </div>

              <p className="book__cover-espert">
                Project description
                <br></br>
                <span>
                  <i className="fa fa-facebook"></i>
                  <i className="fa fa-twitter"></i>
                  <i className="fa fa-linkedin"></i>
                  <i className="fa fa-instagram"></i>
                </span>
              </p>
            </div>

            <div className="book_content">
              <p>
                <span className="drop-cap">o</span>
                Inner Text - Para 1
              </p>
              <p>Inner Text - Para 2</p>
            </div>
          </div>
        </div>

        {/* <div onClick={this.handleToggle}>
          <i className="fa fa-ellipsis-h"></i>
           <i className="fa-ellipsis-h" /> 
        </div>  */}

        {/* <ul className="profile-info additional-profile-info-list" ref="toggle">
          <li>
            <span className="info-email">Office Email</span> me@shuvohabib.com
          </li>
        </ul> */}
        {/* <div className="ellipsis-click" onClick={this.handleToggle}>
          <i className="fa-ellipsis-h" />
        </div> */}
      </>
    );
  }
}

