import React, { useRef, Component } from "react";
import ReactDOM from "react-dom";
// import web from "../Images/d0a103.png";
// import { useHistory } from "react-router-dom";
// import { BrowserRouter, Link, NavLink } from "react-router-dom";

class Card extends Component {
  constructor(props) {
    super(props);
    this.cardRefs = React.createRef();
    // this.handleClick = this.handleClick.bind(this);
  }
  // state = {
  //   props: this.props,
  // };

  componentDidMount() {
    const rect = this.cardRefs.current.getBoundingClientReact();
    console.log(rect);
  }

  handleClick = (event) => {
    const card = event.currentTarget;
    console.log(card);
    <span ref={this.cardRefs}>
      {React.cloneElement(this.cardRefs, this.props)}
    </span>;
    // React.cloneElement(card, this.props);
    const { top, left, width, height } = ReactDOM.findDOMNode(
      this.cardRefs
    ).getBoundingClientRect();
    console.log(this.ref.inner.clientHeight);
  };

  render() {
    return (
      <div
        className="col-md-4 col-10 mx-auto"
        ref={this.cardRefs}
        onClick={this.handleClick}
      >
        <div className="card">
          <img
            src={this.props.imgsrc}
            className="card-img-top"
            alt={this.props.imgsrc}
          />
          <div className="card-body ">
            <h5 className="card-title font-weight-bold">{this.props.title}</h5>
            <p className="card-text">{this.props.text}</p>

            <a
              href={this.props.url}
              target="_blank"
              className="btn btn-primary"
            >
              View Code
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default Card;

