import React, { useRef, Component } from "react";
import ReactDOM from "react-dom";
import "../Card.css";
import $ from "jquery";

class Card extends Component {
  constructor(props) {
    super(props);

    this.cardRefs = React.createRef();
    this.handleClick = this.handleClick.bind(this);
    // this.getClone = this.getClone.bind(this);
  }

  state = {
    active: false,
  };

  handleClick = (event) => {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
  };

  getClone = () => {
    $(".card").fadeToggle(".card");
  };
  render() {
    console.log(this.props);
    console.log(this.state.active);
    return (
      <>
        <div
          className="col-md-4 col-10 mx-auto card"
          onClick={this.handleClick}
        >
          <div
            onClick={this.getClone}
            className={this.state.active ? null : "card"}
            ref={this.cardRefs}
          >
            <img
              src={this.props[0].imgsrc}
              className="card-img-top"
              alt={this.props[0].imgsrc}
            />
            <div className="card-body">
              <h5 className="card-title font-weight-bold">
                {this.props[0].title}
              </h5>
              <p className="card-text">{this.props[0].text}</p>

              <a
                href={this.props[0].url}
                target="_blank"
                className="btn btn-primary"
              >
                View Code
              </a>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Card;

