import React, { useRef, Component } from "react";
import ReactDOM from "react-dom";
// import web from "../Images/d0a103.png";
// import { useHistory } from "react-router-dom";
// import { BrowserRouter, Link, NavLink } from "react-router-dom";

class Card extends Component {
  constructor(props) {
    super(props);

    this.cardRefs = React.createRef();
    this.handleClick = this.handleClick.bind(this);
  }

  state = {
    active: false,
  };

  handleClick = (event) => {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
    // const card = event.currentTarget;
    // console.log(card, this.props);
    // const cardClone = React.cloneElement(this.cardRefs);
    // console.log(cardClone);
    // const {
    //   top,
    //   left,
    //   width,
    //   height,
    // } = this.cardRefs.current.getBoundingClientRect();
    // const cloneStyle = {
    //   position: "fixed",
    //   top: top + "px",
    //   left: left + "px",
    //   width: width + "px",
    //   height: height + "px",
    //   backgroundColor: "red",
    // };
    // const cardStyle = {
    //   opacity: "0",
    // };
  };

  getClone = () => {
    this.handleClick();
  };

  render() {
    console.log(this.props);
    console.log(this.state.active);
    return (
      <>
        <div
          className="col-md-4 col-10 mx-auto card"
          onClick={this.handleClick}
        >
          <div
            className={this.state.active ? "clicked" : " cloneStartStyle"}
            // style={this.state.active ? this.props[1] : null}
            ref={this.cardRefs}
          >
            <img
              src={this.props[0].imgsrc}
              className="card-img-top"
              alt={this.props[0].imgsrc}
            />
            <div className="card-body">
              <h5 className="card-title font-weight-bold">
                {this.props[0].title}
              </h5>
              <p className="card-text">{this.props[0].text}</p>

              <a
                href={this.props[0].url}
                target="_blank"
                className="btn btn-primary"
              >
                View Code
              </a>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Card;

