import React, { useRef, Component } from "react";
import ReactDOM from "react-dom";
// import web from "../Images/d0a103.png";
// import { useHistory } from "react-router-dom";
// import { BrowserRouter, Link, NavLink } from "react-router-dom";

class Card extends Component {
  constructor(props) {
    super(props);

    this.cardRefs = React.createRef();
    this.handleClick = this.handleClick.bind(this);
  }

  state = {
    active: false,
    cloneStyle: {
      position: null,
      top: 0,
      left: 0,
      width: 0,
      height: 0,
    },
  };

  handleClick = (event) => {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
    // const card = event.currentTarget;
    // console.log(card, this.props);
    // const cardClone = React.cloneElement(this.cardRefs);
    // console.log(cardClone);
  };

  getStartClone = () => {
    const {
      top,
      left,
      width,
      height,
    } = this.cardRefs.current.getBoundingClientRect();

    this.setState(
      (this.state.cloneStyle = {
        position: "fixed",
        top: top + "px",
        left: left + "px",
        width: "350px",
        height: "500px",
        backgroundColor: "red",
      })
    );

    const cardStyle = {
      opacity: "0",
    };
  };

  getClone = () => {
    this.handleClick();
  };

  render() {
    console.log(this.props);
    console.log(this.state.active);
    return (
      <>
        <div className="col-md-4 col-10 mx-auto" onClick={this.handleClick}>
          <div
            className={
              this.state.active ? this.props[1] : this.state.cloneStyle
            }
            // style={this.state.active ? this.props[1] : null}
            ref={this.cardRefs}
          >
            <img
              src={this.props[0].imgsrc}
              className="card-img-top"
              alt={this.props[0].imgsrc}
            />
            <div className="card-body">
              <h5 className="card-title font-weight-bold">
                {this.props[0].title}
              </h5>
              <p className="card-text">{this.props[0].text}</p>

              <a
                href={this.props[0].url}
                target="_blank"
                className="btn btn-primary"
              >
                View Code
              </a>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Card;

