import React, { useRef, Component } from "react";
import ReactDOM from "react-dom";
// import web from "../Images/d0a103.png";
// import { useHistory } from "react-router-dom";
// import { BrowserRouter, Link, NavLink } from "react-router-dom";

class Card extends Component {
  constructor(props) {
    super(props);

    this.cardRefs = React.createRef();
    this.handleClick = this.handleClick.bind(this);
  }

  state = {
    active: false,
  };

  handleClick = async (event) => {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
    const card = event.currentTarget;
    console.log(card, this.props);
    const cardClone = React.cloneElement(card);
    console.log(cardClone);
    const {
      top,
      left,
      width,
      height,
    } = this.cardRefs.current.getBoundingClientRect();
    const cloneStyle = {
      position: "fixed",
      top: top + "px",
      left: left + "px",
      width: width + "px",
      height: height + "px",
      backgroundColor: "red",
    };
    const cardStyle = {
      opacity: "0",
    };
  };

  getClone = () => {
    return <div style={Object.assign(this.cloneStyle, this.cardStyle)}></div>;
  };

  render() {
    return (
      <React.Fragment>
        <div className="card" ref={this.cardRefs}>
          <img
            src={this.props.imgsrc}
            className="card-img-top"
            alt={this.props.imgsrc}
          />
          <div className="card-body" style={{ color: this.props.demo }}>
            <h5 className="card-title font-weight-bold">{this.props.title}</h5>
            <p className="card-text">{this.props.text}</p>

            <a
              href={this.props.url}
              target="_blank"
              className="btn btn-primary"
            >
              View Code
            </a>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Card;

