import React, { Component } from "react";
import web from "../Images/d0a103.png";
// import { useHistory } from "react-router-dom";
import { BrowserRouter, Link, NavLink } from "react-router-dom";
import { style as social } from "https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css";
import $ from "https://code.jquery.com/jquery-3.5.1.js";

class Card extends Component {
  componentDidMount() {
    const h1 = $(".book").on("click", function () {
      $(this).toggleClass(".book--expanded");
    });
  }
  render(props) {
    return (
      <div class="wrapper">
        <div className="book">
          <div className="book__cover">
            <div className="header-image">
              <div class="overlay"></div>
            </div>
            <div className="title-wrap">
              <h1 className="article-title">EcoSwap</h1>
            </div>

            <p class="book__cover-espert">
              Project description
              <br></br>
              <span className="social">
                <i className="fa fa-facebook"></i>
                <i className="fa fa-twitter"></i>
                <i className="fa fa-linkedin"></i>
                <i className="fa fa-instagram"></i>
              </span>
            </p>
          </div>
          <div className="book_content">
            <p>
              <span className="drop-cap">o</span>
              Inner Text - Para 1
            </p>
            <p>Inner Text - Para 2</p>
          </div>
        </div>
      </div>
    );
  }
}

